#!/usr/bin/env python# -*- coding: utf-8 -*-import MySQLdbclass home_mysql:    con = None        def connect(self):        self.con = MySQLdb.connect('localhost', 'home', 'halloha234', 'home');        def cursor(self):        try:            return self.con.cursor()        except (AttributeError, MySQLdb.OperationalError):            self.connect()            return self.con.cursor()                def query(self, query):        cur = self.con.cursor()        cur.execute(query)        self.con.commit() # Make sure data is committed to the database        cur.close()        def __enter__(self):        return self        def __exit__(self):        self.con.close()    def __del__(self):        self.con.close()